<script>
import Tooltip from '../components/Tooltip.vue'

export default {
  name: 'LinkItem',
  components: {
    Tooltip
  },
  props: ['item'],
  methods: {
    build_img_path(type, logo){
      return new URL(`../images/links/${type}/${logo}`, import.meta.url).href;
      //return `/images/links/${type}/${logo}`
    },
    build_wx_img_path(type, logo){
      return new URL(`../images/links/${type}/weixin/${logo}`, import.meta.url).href;
      //return `/images/links/${type}/weixin/${logo}`
    },
    upper(str){
      return str.toUpperCase()
    },
    first_word(str){
      return str.charAt(0).toUpperCase()
    }
  }
}
</script>
